package javaexamples1;

public class Javapyramid {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i = 1; i < 7; i++) {
			for (int j = 0; j < i; j++) {
				String str=" ";
				str=str+"*";
				
				System.out.print(str);

			}System.out.println();
		}

	}

}
