public class ContentEquals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			
		String str1="kongara chaitanya";
		String str2="kongara";
		if(str1.contentEquals(str2))
			{
				System.out.println("Equal");
			
		}else{
			System.out.println("NOt Equal");
		}
		
	}

}
